INSERT INTO `product` (`id`, `position`, `url`, `name`, `articul`, `price`, `currency_id`, `price_old`, `notice`, `content`, `visible`, `quantity`, `image`) VALUES
(1, 1, 'dune', 'Дюна', 'B0001', '600.00', 1, '600.00', 'Фантастика от Фрэнка Герберта', 'Дюна повествует о далеком будущем, где могущественные дома сражаются за контроль над пустынной планетой Арракис. Эта планета является единственным источником мощной пряности меланжи, способной удлинять жизнь и улучшать способности. Пауль Атрейдес, главный герой романа, сталкивается с предательством и опасностями, вовлеченный в великий конфликт. В ходе повествования, он открывает свои уникальные способности и предначертанную судьбу. Герберту удалось создать богатый мир со сложной политикой и философией, который привлекает читателей уже десятилетия. Дюна — не просто история, а эпическая сага о власти, предательствах и судьбе.', 1, 5, 'https://img4.labirint.ru/rc/a9d36b19aa7ab730def9f076634b5815/220x340q80/books83/828202/cover.jpg?1634369118'),
(2, 1, 'foundation', 'Основание', 'B0002', '400.00', 1, '450.00', 'Фантастика от Айзека Азимова', 'Основание рассказывает о масштабной эпической историй зарождения новой цивилизации на руинах старой галактической империи. Харри Селдон, гениальный математик, предсказывает неизбежный крах империи и создает «План Селдона», чтобы сократить мрачный период хаоса. Этот план ведет к созданию Основания, собрания ученых и инженеров, которые станут хранителями знаний. В то время как империя погружается в разруху, Основание сталкивается с внутренними и внешними угрозами. Азимов мастерски изображает общественные и политические механизмы, развивая глубокие мысли о науке и человеческой природе. Основание остается культовым произведением научной фантастики.', 1, 7, 'data:image/jpeg;base64,'),
(3, 2, 'brief-history-time', 'Краткая история времени', 'B0003', '300.00', 1, '350.00', 'Научная литература от Стивена Хокинга', 'В этой книге Стивен Хокинг объясняет сложные концепции времени и пространства доступным языком. Он раскрывает тайны черных дыр, Большого взрыва и природы времени, делая их понятными для широкого круга читателей. Показывая, как работает наша вселенная, Хокинг также открывает перед читателями удивительные теории о квантовой механике и гравитации. Краткая история времени стала не просто бестселлером, но и культовой книгой, меняющей взгляд на мир. Невероятные иллюстрации и примеры помогают усвоить материал. Эта книга стала проводником в мир современной науки для миллионов читателей.', 1, 0, 'data:image/jpeg;base64,'),
(4, 2, 'origin-of-species', 'Происхождение видов', 'B0004', '350.00', 1, '400.00', 'Научная литература от Чарльза Дарвина', 'Происхождение видов — фундаментальный научный труд, в котором Чарльз Дарвин изложил свою теорию эволюции. В книге подробно обсуждаются механизмы естественного отбора и приспособлений, которые позволили различным видам выживать и развиваться. Дарвин детально анализирует исследования, проводимые в различных регионах мира, и приводит примеры изменчивости видов. Этот труд не только изменил научное сообщество, но и вызвал глубокие общественные дебаты. Для книги характерен академический стиль и множество примеров из природного мира. Происхождение видов остается одним из краеугольных камней биологической науки и по сей день.', 1, 4, 'https://cdn.ast.ru/v2/ASE000000000832788/COVER/cover1__w340.jpg'),
(5, 3, 'sherlock-holmes', 'Шерлок Холмс', 'B0005', '450.00', 1, '500.00', 'Детектив от Артура Конан Дойла', 'Шерлок Холмс - это культовый детективный персонаж, созданный Артуром Конан Дойлом. Каждый рассказ и роман о Холмсе наполнен захватывающими расследованиями и удивительными дедуктивными методами. Шерлок Холмс и его верный помощник доктор Ватсон раскрывают самые запутанные преступления, использую ум и логику. Захватывающие приключения и яркие персонажи делают эти рассказы поистине незабываемыми. Конан Дойл умело сочетает элементарные элементы триллера и мастерский стиль повествования. Холмс остается любимым персонажем миллионов читателей по всему миру.', 1, 5, 'https://img2.labirint.ru/rcimg/4490b12e91e968d414b644408792f02f/960x540/books73/726120/ph_001.jpg?1573896352'),
(6, 3, 'the-big-sleep', 'Глубокий сон', 'B0006', '280.00', 1, '320.00', 'Детектив от Рэймонда Чандлера', 'Глубокий сон — классический детективный роман от Рэймонда Чандлера с филиппом Марло, частным детективом, в главной роли. Марло сталкивается с жестокой преступной средой Лос-Анджелеса, запутанными делами и фатальными женщинами. Он должен распутать клубок лжи и коррумпированных дел, полагаясь на остроумие и готовность рисковать. Чандлер мастерски описывает атмосферу большой американской города с его пригородами и грязными улицами. Тонкая психологическая проработка персонажей и неожиданные повороты делают этот роман поистине увлекательным. Глубокий сон остается культовой книгой жанра нуар.', 1, 0, 'data:image/jpeg;base64,'),
(7, 1, 'martian', 'Марсианин', 'B0007', '380.00', 1, '380.00', 'Фантастический роман от Энди Вейера', 'Марсианин — это захватывающий роман о выживании Марка Уотни, космонавта, оставшегося на Марсе в результате аварии. Марк использует свои инженерные и ботанические навыки, чтобы адаптироваться к суровым условиям планеты. Он находит способы получить воду, еду и поддерживать связь с Землей. Эта книга погружает читателя в мир научных открытий и изобретений. Подробные описания технологических решений и научных методов делают повествование реалистичным и увлекательным. История Марка — это история борьбы, решимости и человеческого духа.', 1, 0, 'https://img3.labirint.ru/rc/f06417ce16bd3020d4d44eb910cedd4e/363x561q80/books46/453409/cover.jpg?1613060771'),
(8, 2, 'cosmos', 'Космос', 'B0008', '360.00', 1, '400.00', 'Научная литература от Карла Сагана', 'Космос — это классический научно-популярный труд Карла Сагана, который открывает перед читателем удивительный мир астрономии и космологии. Саган объясняет сложные концепции вселенной простым и доступным языком, используя метафоры и аналогии. Книга охватывает широкий круг тем: от истории науки до поиска внеземной жизни. Космос вдохновляет на философские размышления о нашем месте во вселенной. Сочетание научных данных и поэтического стиля делает книгу настоящим шедевром. Это произведение остается актуальным и читаемым десятилетия спустя после своего выхода.', 1, 0, 'https://img4.labirint.ru/rc/f88452f25e74217554f6e8ab7462dd91/363x561q80/books41/402528/cover.jpg?1563723572'),
(9, 3, 'gone-girl', 'Исчезнувшая', 'B0009', '370.00', 1, '370.00', 'Детектив от Гиллиан Флинн', 'Исчезнувшая — это захватывающий психологический триллер, который ни на минуту не отпустит читателя. Ник и Эми Данн — супружеская пара с тайнами, которые вскрываются, когда Эми неожиданно исчезает. Повествование ведется глазами обоих героев, и правда постепенно выявляется через их противоречивые рассказы. Автор умело балансирует на грани между детективом и психологической драмой. Сюжет наполнен неожиданными поворотами и тонкой психологией. Исчезнувшая — это книга о лжи, манипуляциях и сложных человеческих отношениях, которая оставляет глубокий след в сознании.', 1, 30, 'https://img2.labirint.ru/rcimg/81f03d5e3f3dc2710d3bee1ef8809d6e/960x540/books92/914100/ph_001.jpg?1670563524'),
(10, 1, 'neuromancer', 'Нейромант', 'B0010', '450.00', 1, '450.00', 'Киберпанк от Уильяма Гибсона', 'Нейромант — это культовый роман, заложивший основы жанра киберпанк. Книга повествует о киберпространстве, искусственном интеллекте и высокотехнологичных мирах будущего. Гибсон создает мрачный, антиутопический ландшафт, где хакеры и крупные корпорации ведут постоянную борьбу. Главный герой Кейса нанимают для сложного взлома виртуальной системы, что запускает серию событий с непредсказуемыми последствиями. Богатый язык и оригинальные идеи сделали книгу классикой научной фантастики. Нейромант продолжает вдохновлять писателей, кинематографистов и геймеров по всему миру.', 1, 2, 'https://litres.ru/pub/c/cover/8738832.jpg');

